class DclInst:
    def __init__(self, camera, lens):
        self.camera = camera
        self.lens = lens
dclinst = DclInst(0,0)

class DclCamera:
    def __init__(self, model, dark, satu, rdsu, gdsu, bdsu, nx, ny, dx, dy, exte, lens):
        self.model = model
        self.dark = dark
        self.satu = satu
        self.rdsu = rdsu
        self.gdsu = gdsu
        self.bdsu = bdsu
        self.nx = nx
        self.ny = ny
        self.dx = dx
        self.dy = dy
        self.exte = exte
        self.lens = lens

class DclLens:
    def __init__(self, model, aper, corr, radi, vign):
        self.model = model
        self.aper = aper
        self.corr = corr
        self.radi = radi
        self.vign = vign

class DclData:
    def __init__(self, V, M, R, G, B, D, img, fce, expo, dark, max):
        self.V = V
        self.M = M
        self.R = R
        self.G = G
        self.B = B
        self.D = D
        self.img = img
        self.fce = fce
        self.expo = expo
        self.dark = dark
        self.max = max

class DclExpo:
    def __init__(self, iso, aperture, shutter):
        self.iso = iso
        self.aperture = aperture
        self.shutter = shutter

